#!/usr/bin/env python3
#basic stuffs with sets
myset=set('AACCCCCG')
myset2={'AGGGGCCCC'}
print(myset)
print(myset2)
setA = {3, 14, 15, 9, 26, 5, 35, 9}
setB = {60, 22, 14, 0, 9}
print(setA, setB)
inters = setA & setB
diff = setA - setB
union = setA | setB
symm_diff = setA ^ setB
print('The intersection is:',inters,'\nThe difference is:',diff,'\nThe union is:',union,'\nThe symmetric difference is',symm_diff)
#dna and sets
dna = ''
dic_dna={}
set_dna =set(dna)
print(set_dna)
nt_count=0
for nt in set_dna:
  print(nt)
  count=dna.count(nt)
  dic_dna[nt]=count
  nt_count += count
print(dic_dna)
#calculating gc content
gc_perc=(dic_dna['G'] + dic_dna['C'])/nt_count
print('GC content = ',gc_perc*100,'%')


